import React from "react";
import { render } from "react-dom";

const styles = {
  fontFamily: "sans-serif",
  textAlign: "left"
};


class ExpensesTable extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      rows: []
    };
  }
  handleAddRow = () => {

    this.setState((prevState, props) => {
      const row = <div> <input/> </div>
      return { rows: [...prevState.rows, row] };
    });
  };

  handleRemoveRow = () => {
    this.setState((prevState, props) => {
      return { rows: prevState.rows.slice(1) };
    });
  };

  render() {
    return (
      <div style={styles}>
        <table>
          <tbody>
            {this.state.rows.map((row, index) => (
              <tr key={index}>
                <td >
                    <input className="form-control" style={{height: '25px'}} name="expenses" type="text"  placeholder="expenses" value={this.state.expenses} onChange={this.onInputchange} required />
                </td>
              </tr>
            ))}
            <tr>
              <td className="" onClick={this.handleAddRow}>
                (+)
              </td>
              {Boolean(this.state.rows.length) && (
                <td onClick={this.handleRemoveRow}>(-)</td>
              )}
            </tr>
          </tbody>
        </table>
      </div>
    );
  }
}

export default ExpensesTable
