const INITIAL_STATE = {
  total: 0
}


export default function(state=INITIAL_STATE , action){
  switch(action.type){
    case 'ADD_EXPENSES_TOTAL':
    console.log("Current payload ", action.payload)
    console.log("Current state ", state)
        return state.total + action.payload

    default:
      return state
  }
}
