const INITIAL_STATE={
  id: 0,
  totalIncome: 0,
  saving: 0
}




export default function(state= INITIAL_STATE , action){
  switch(action.type){
    case 'EXPENSES':
        return { expenses : action.payload}

    default:
      return state
  }
}
