import React from "react";
import { render } from "react-dom";

import { connect } from 'react-redux'

import ProductTable from '../ProductTable'

import ProductRow from '../ProductRow'



import { updateExpenses , addToSumExpenses  } from '../../../actions'

let id = 0

class Products extends React.Component {

  constructor(props) {
    super(props);

    this.state = {};
    this.state.filterText = "";
    this.state.products = [  { id: 1, name: '', price: '' } ];

  }
  handleUserInput(filterText) {
    this.setState({filterText: filterText});
  };

  handleRowDel(product) {
    let index = this.state.products.indexOf(product);
    this.state.products.splice(index, 1);
    this.setState(this.state.products);
  };

  handleAddEvent(evt) {
    let id = (+ new Date() + Math.floor(Math.random() * 999999)).toString(36);
    let product = { id: id, name: "", price: "" }
    this.state.products.push(product);
    this.setState(this.state.products);

    // console.log("CURRENT PRICE TOTAL", this.props.expensesSum)
    // console.log("NEW ADDITION" , parseInt(this.state.products[this.state.products.length-2].price )    )


    this.props.addToSumExpenses( parseInt(this.state.products[this.state.products.length-2].price ))
    //call new reducer

  }

  handleProductTable(evt) {
    let item = { id: evt.target.id, name: evt.target.name, value: evt.target.value };
    let products = this.state.products.slice();
    let newProducts = products.map(function(product) {

    for (var key in product) {
      if (key == item.name && product.id == item.id) {
        product[key] = item.value;

      }
    }
    return product;
  });
    this.setState({products:newProducts});
    this.props.updateExpenses(this.state.products)
  };
  render() {

    const handleClick = (evt)=> { if (evt.which === 13) { this.handleAddEvent(evt) } }

    return (
      <div onKeyDown={handleClick}>
        <ProductTable onProductTableUpdate={this.handleProductTable.bind(this)} onRowAdd={this.handleAddEvent.bind(this)} onRowDel={this.handleRowDel.bind(this)} products={this.state.products} filterText={this.state.filterText}/>
      </div>
    );

  }

}


const mapStateToProps = (state) => {
  return {expensesSum : state.expensesSum.total}
}

export default connect( mapStateToProps, { updateExpenses, addToSumExpenses  }  )(Products)
