const INITIAL_STATE={
  id: 0,
  totalIncome: 0,
  products: [{}],
  saving: 0
}




export default function(state= INITIAL_STATE , action){
  switch(action.type){
    case 'CALCULATOR':
      return action.payload
    case 'EXPENSES':
    return { products : action.payload}
    default:
      return state
  }
}



